public static Class<?> loadClassByPath(String[] paths, String className) throws Exception {

        // 1. 将路径转换为 URL 对象（适配不同操作系统）
        URL[] urls = new URL[paths.length];
        for (int i = 0; i < paths.length; i++) {
            String normalizedPath = paths[i].replace("/", File.separator);
            urls[i] = new File(normalizedPath).toURI().toURL();
        }
        URLClassLoader loader = new URLClassLoader(urls);

        // 2. 加载目标类
        return loader.loadClass(className);
    }

