package com.huawei.clouddragon.accessmgmt.model;

import com.huawei.clouddragon.accessmgmt.context.RequestContext;
import com.huawei.clouddragon.accessmgmt.servicedto.extras.AbstractUserExtrasProcessor;

import lombok.Getter;
import lombok.Setter;

/**
 * 用户信息
 *
 * @since 2024-08-19
 */
@Getter
@Setter
public class SimpleUser extends AbstractUserExtrasProcessor {
    private String name;

    private String nickName;

    private String id;

    private String enterpriseId;

    private String userType;

    private String domainId;

    private String domainName;

    @Override
    public void updateUserNickName() {
        if (this.nickName == null) {
            return;
        }
        String nickNameByRegion = parseNickNameByRegion(this.nickName, RequestContext.getCurrentRegion());

        // 昵称为null或者空字符串也设置, 因为用户可能在当前region没设置过昵称
        this.setNickName(nickNameByRegion);
    }
}

